application/json.example:
{
    // The type of advertisement: must be either "banner" or "product"
    "ad_type": "product", // banner || product
    "ad": {
        "title": "Summer Sale Banner",

        // Banner-specific
        "banner_type": "banner", // banner || slideshow
        "banner_location": "header", // header || footer
        "target_url": "https://example.com/sale",

        "starts_at": "2025-10-01", // must be today or later
        "ends_at": "2025-12-31", // must be after starts_at

        "budget_type": "daily", // daily || monthly
        "budget_amount": 100.00,

        // Array of at least 3 keywords to target
        "target_keywords": [
            "summer",
            "sale",
            "discount"
        ],
        // Product-specific: required if ad_type is "product"
        "product_id": 1
    },

    // Array of image files for the banner (required if ad_type is "banner")
    // Each file should be an image, max 5MB (5120 KB)
    "media": [
        /* file upload: banner1.jpg */ ,
        /* file upload: banner2.png */
    ]
}

{
    "date_from": "2025-02-01",
    "date_to": "2025-12-30",
    // "starts_at": "2025-12-30", // Ad Start Date
    // "ends_at": "2025-02-30", Ad End Date
    // "status": "active", // pending || active || in_active || finished
    "format": "xlsx" // csv || xlsx
}
